import React, { useState, useEffect } from 'react';
import Filters from '../../components/Filters';
import './styles.css';
import { barOptions, pieOptions} from './chart-options';
import Chart from 'react-apexcharts';
import axios from 'axios';
import { buildBarSeries, getPlatformChartData, getGenderChartData } from './helpers';


type PieChartData = {
    labels: string [];
    series: number [];
}

type BarChartData = {
    x: string;
    y: number;
}

const inicialPieData ={
    labels: [],
    series: []
}

const BASE_URL = 'http://localhost:8080'

const Charts = () => {
        const [barChartData, setBarChartData] = useState<BarChartData[]>([]);
        const [platformData, setPlatformData] = useState<PieChartData>(inicialPieData);
        const [genderData, setGenderData] = useState<PieChartData>(inicialPieData);

        useEffect (() =>{
            async function getData () {
               
                 const recordsResponse = await axios.get(`${BASE_URL}/records`);
                 const gamesResponse = await axios.get(`${BASE_URL}/games`);

                 const barData = buildBarSeries(gamesResponse.data,recordsResponse.data.content)
                 setBarChartData(barData);

                 const platformChartData = getPlatformChartData(recordsResponse.data.content);
                 setPlatformData(platformChartData);

                 const genderData = getGenderChartData(recordsResponse.data.content);
                 setGenderData(genderData);
            }

            getData();
        }, [] )

    return (
        <div className="page-container">
            <Filters link="/records" linkText= "VER TABELA" />
            <div className="chart-container">
                <div className="top-related">
                    <h1>
                        Jogos mais Votados
                    </h1>
                    <div className="games-container">
                        <Chart 
                        options={barOptions}
                        type="bar" 
                        width="600"
                        height="500"
                        series={[{data: barChartData}]}
                        />
                    </div>
                </div>
                <div className="charts">
                    <div className="platform-chart">
                        <h2 className="chart-title">Plataformas</h2>
                        <Chart 
                        options={{...pieOptions, labels: platformData?.labels}}
                        type = "donut"
                        series={platformData?.series}
                        width="300"
                        />
                    </div>
                    <div className="gender-chart">
                        <h2 className="chart-title">Gêneros</h2>
                        <Chart 
                        options={{...pieOptions, labels: genderData?.labels}}
                        type = "donut"
                        series={genderData?.series}
                        width="300"
                        />
                    </div>

                </div>
            </div>
        </div>
    )

}

export default Charts;
